class Recipe{constructor(i){this.id=i.id,this.name=i.name,this.image=i.image,this.servings=i.servings,this.ingredients=i.ingredients,this.time=i.time,this.description=i.description,this.appliance=i.appliance,this.ustensils=i.ustensils}getFormattedIngredients(){return this.ingredients.map(i=>{var t=i.quantity?": "+i.quantity:"",e=i.unit?" "+i.unit:"";return i.ingredient+t+e}).join(", ")}getFormattedUstensils(){return this.ustensils.join(", ")}}export{Recipe};